import { createContext, useState, useEffect, useContext } from "react";

const WeatherContext = createContext();

export const WeatherProvider = ({ children }) => {
    const [weather, setWeather] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const apiKey = '8301a1d724014fac903131548240411';
        const city = 'Istanbul';
        const url = `http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}&aqi=no`;

        // Axios ile veri çekme
        const fetchWeather = async () => {
          setLoading(true);
          try {
            const response = await axios.get(url);
            setWeatherData(response.data); // API yanıtını state’e kaydet
          } catch (err) {
            setError(err.message); // Hata varsa state'e kaydet
          } finally {
            setLoading(false);
          }
        };

        fetchWeather();
      }, []);
      return (
        <WeatherContext.Provider value={{ weather, loading, error }}>
          {children}
        </WeatherContext.Provider>
      );
    }

    export const useWeather = () => useContext(WeatherContext);
