import { useContext, useState, useEffect } from "react";
import axios from "axios";
import { CountryContext } from "../context/CountryContext";

function CityComponent() {
  const { selectedCountry } = useContext(CountryContext);
  const [cities, setCities] = useState([]); // Başlangıç değeri boş bir dizi
  const [selectedCity, setSelectedCity] = useState("");

  useEffect(() => {
    // Sadece selectedCountry değiştiğinde API çağrısı yap
    if (selectedCountry) {
      const fetchCities = async () => {
        try {
          const response = await axios.post("https://countriesnow.space/api/v0.1/countries/cities", {
            country: selectedCountry, // Doğru yapı
          }, {
            headers: {
              "Content-Type": "application/json",
            },
          });

          // API yanıtından şehirleri ayıklayın
          setCities(response.data.data); // response.data.data'dan şehirleri alın
        } catch (error) {
          console.error("Şehir verileri alınamadı:", error);
        }
      };

      fetchCities();
    }
  }, [selectedCountry]); // selectedCountry değiştiğinde bu useEffect tetiklenir

  return (
    <div>
      <h2>{selectedCountry} Ülkesine Ait Şehirler:</h2>
      {cities.length > 0 ? (
        <ul>
          {cities.map((city) => (
            <li key={city}>{city}</li> // Şehirleri listele
          ))}
        </ul>
      ) : (
        <p>Henüz şehir verisi yok.</p>
      )}
    </div>
  );
}

export default CityComponent;
