import { useContext } from 'react';
import { WeatherContext } from '../context/WeatherContext';
import '../App.css';

function WeatherComponent() {
  const { weather } = useContext(WeatherContext);
  const { countries } = useContext(WeatherContext);
  const { selectedCountry } = useContext(WeatherContext);

  return (
    <div>
      {weather && (
        <div>
          <div className='RegionName'>
            <h2>{selectedCountry}/{weather.location.name}</h2>
            <div className='Cities'>
              {/* Şehir seçim menüsü buraya eklenebilir */}
              <select className='small-select'>
                {/* Şehirlerin burada listeleneceği varsayılıyor */}
                <option value="Istanbul">Istanbul</option>
                <option value="Ankara">Ankara</option>
                {/* Diğer şehirler buraya eklenebilir */}
              </select>
            </div>
          </div>
          <div className='Weather'>
            {weather.forecast.forecastday.map((day) => (
              <div key={day.date} className='weather-box'>
                <h3>{day.date}</h3>
                <p>{day.day.condition.text}</p>
                <img src={day.day.condition.icon} alt={day.day.condition.text} />
                <p>Max: {day.day.maxtemp_c}°C</p>
                <p>Min: {day.day.mintemp_c}°C</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default WeatherComponent;
