// src/WeatherComponent.js
import React from 'react';
import { useWeather } from './WeatherContext';

const WeatherComponent = () => {
  const { weatherData, error, loading } = useWeather();

  if (loading) return <p>Yükleniyor...</p>;
  if (error) return <p>Hata: {error}</p>;

  return (
    <div>
      <h1>Hava Durumu</h1>
      {weatherData && (
        <div>
          <p>Şehir: {weatherData.location.name}</p>
          <p>Sıcaklık: {weatherData.current.temp_c}°C</p>
          <p>Hava Durumu: {weatherData.current.condition.text}</p>
          <img src={weatherData.current.condition.icon} alt="Hava durumu ikonu" />
        </div>
      )}
    </div>
  );
};

export default WeatherComponent;
