// src/WeatherContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';

const WeatherContext = createContext();

export const WeatherProvider = ({ children }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const apiKey = '8301a1d724014fac903131548240411';
    const city = 'Istanbul';
    const days = 7;  // 7 günlük tahmin almak için gün sayısı
    const url = `http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${city}&days=${days}`;

    const fetchWeather = async () => {
      setLoading(true);
      try {
        const response = await axios.get(url);
        setWeatherData(response.data); // API yanıtını state’e kaydet
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  return (
    <WeatherContext.Provider value={{ weatherData, error, loading }}>
      {children}
    </WeatherContext.Provider>
  );
};

export const useWeather = () => useContext(WeatherContext);
