import {useContext, useState} from 'react';
import {WeatherContext} from '../context/WeatherContext';

function WeatherComponent() {
  const {weather} = useContext(WeatherContext);

  return (
    <div>
    <h1>1 Haftalık Hava Durumu Tahmini</h1>
    {weatherData && (
      <div>
        <p>Şehir: {weatherData.location.name}</p>
        {weatherData.forecast.forecastday.map((day) => (
          <div key={day.date}>
            <h3>{day.date}</h3>
            <p>En Yüksek Sıcaklık: {day.day.maxtemp_c}°C</p>
            <p>En Düşük Sıcaklık: {day.day.mintemp_c}°C</p>
            <p>Hava Durumu: {day.day.condition.text}</p>
            <img src={day.day.condition.icon} alt="Hava durumu ikonu" />
          </div>
        ))}
      </div>
    )}
  </div>
);
};
export default WeatherComponent;
